mapper.select:
SELECT course_id, course_name, users.name AS teacher_name FROM courses, users
WHERE
    teacher_id=uid AND
    users.role='teacher' AND
    course_id in (
        SELECT course_id FROM student_course
        WHERE
            student_id=#{uid} AND
            `status`=#{status}
    );

SELECT course_id, course_name, users.name AS teacher_name FROM courses, users
WHERE
    teacher_id=uid AND
    users.role='teacher' AND
    course_id in (
        SELECT course_id FROM student_course
        WHERE
            student_id=#{uid}
    );

mapper.update:
UPDATE student_lesson SET
                        view_seconds=#{currentTime}
                    WHERE
                        student_id=#{uid} AND
                        lesson_id=#{lessonId};
